#[cfg(test)]
mod tests {
    use crate::longest_palindrome;

    #[test]
    fn example_1() {
        assert_eq!(
            String::from("bab"),
            longest_palindrome(String::from("babad"))
        );
    }

    #[test]
    fn example_2() {
        assert_eq!(String::from("bb"), longest_palindrome(String::from("cbbd")));
    }

    #[test]
    fn long_string() {
        let long_str = "".to_string();
        assert_eq!(long_str, longest_palindrome(long_str.clone()));
    }

    #[test]
    fn really_long_string() {
        let long_str = "".to_string();
        assert_eq!("qahaq", longest_palindrome(long_str))
    }
}

pub fn longest_palindrome(s: String) -> String {
    let s_bytes = s.as_bytes();
    let mut result = String::new();
    for i in 0..s.len() {
        for j in (i..=s.len()).rev() {
            let byte_slice = s_bytes[i..j].to_vec();
            let mut rev_bytes = byte_slice.clone();
            rev_bytes.reverse();

            if result.len() >= j - i {
                break;
            } else if byte_slice == rev_bytes {
                result = String::from_utf8(byte_slice).unwrap();
                break;
            }
        }
    }
    result
}
